mapper.select:
select a.id as id, a.l_name as lName, b.uname as uname, b.head_url as headUrl, a.title as title, a.time as time, a.summary as summary, a.istop as istop from topic as a left join user as b on a.uid=b.id where a.audit!=3
and a.l_id = #{tagId}
and content_filter like concat('%',#{keyword,jdbcType=VARCHAR},'%') or title like concat('%',#{keyword,jdbcType=VARCHAR},'%')
order by a.istop desc,a.time desc limit #{start},#{limit}

update.foreach:
update topic
set audit=#{audit}
where id=${item}

mapper.select:
select a.id,a.l_name,b.uname,a.title,a.time,a.audit,a.istop from topic as a inner join user as b on a.uid = b.id
where audit=#{audit}
order by time asc limit #{start},#{limit}

update.foreach:
update topic
set audit = #{audit}
where id = #{item}

update topic
set istop = #{istop}
where id = #{item}

mapper.select:
select * from topic
where uid= #{uid}
order by time desc
    limit #{start},#{limit}
